irulanclient.directive('pages', ['Pages', function directive$pages(Pages) {
  console.verbose("pages directive")
  //console.verbose($element)
  return {
    restrict: 'E',
    transclude: true,
    link: function directive$pages$link($scope, $element, $attrs, controller, $transclude) {
      console.verbose($element);
      console.verbose(Pages);
      Pages.element = $element;
    },
    template:
      '<div id="pagesworkspace">'+
      '  <div ng-repeat="(name, page) in pages">'+
      '    <page ng-pagename="{{name}}">{{name}}</page>'+
      '    <ng-transclude></ng-transclude>'+
      '  </div>'+
      '</div>',
  }
}])

irulanclient.directive('page', ['$filter', '$timeout', '$compile', function directive$page($filter, $timeout, $compile) {
  return {
    restrict: 'E',
    link: function directive$page$link($scope, $element, $attrs, controller, $transclude) {
      console.verbose('inject page!');
      if (!$transclude) {
        throw minErr('ngTransclude')('orphan',
          'Illegal use of ngTransclude directive in the template! ' +
          'No parent directive that requires a transclusion found. ' +
          'Element: {0}',
          startingTag($element)
        );
      }

      var page = $scope.pages[$attrs['ngPagename']];
      console.verbose($filter('pageRenderer')(page));
      console.verbose($element)
      //$element[0].innerHTML = $filter('pageRenderer')(page);

      var context = {
        page: page,
        pagename: $attrs['ngPagename'],
        scope: $scope,
        element: $element,
        attrs: $attrs,
        filter: $filter,
        controller: controller,
        transclude: $transclude
      }
      $scope.$watch(
        (function directive$page$watch$monitor() {
          return JSON.stringify($scope.pages[context.pagename]);
        }).bind(context),
        (function directive$page$watch$callback() {
          //window.setTimeout(
          $timeout((function directive$page$watch$callback$timeout() {
            console.verbose("page update detected");

            // If we were to simply push the new HTML content into
            //  element[0].innerHTML then any directives it contains (e.g.
            //  "<warning>some text</warning>") would not get processed.
            //  Instead the new content is passed back through the compiler for
            //  further AngularJS processing.  Talking directly to the DOM
            //  (.innerHTML attribute) hides the update from AngularJS.
            var template = $filter('pageRenderer')(context.page);
            var linkFunction = $compile(template);
            var content = linkFunction(context.scope);
            context.element.empty();
            context.element.append(content);

          }).bind(context), 1, true);
        }).bind(context)
      )
    }
  }
}])

irulanclient.directive('warning', function directive$warning() {
  console.verbose("warning directive")
  return {
    restrict: 'E',
    transclude: true,
    template:
      '<md-content class="md-warn">'+
      '<ng-transclude></ng-transclude>'+
      '</md-content>',
  }
})

